import { Component, OnInit } from '@angular/core';
import { ChatService, Message } from 'src/app/chat.service';
import { Observable } from 'rxjs';
import { scan } from 'rxjs/operators';

@Component({
  selector: 'app-chat-eng',
  templateUrl: './chat-eng.component.html',
  styleUrls: ['./chat-eng.component.css']
})
export class ChatEngComponent implements OnInit {

  messages!: Observable<Message[]>;
  formValue!: any;
  sessionVal!: string;
  countVal = 0;
  // tempSVal!: string;
  // cht:any;

  constructor(private chat: ChatService) { }

  ngOnInit(): void {
      // var val = "friday";

      // var val = {
        // "text": "friday"
      // };
      // this.chat.talk(val).subscribe(res => console.log(res));


      // Appends to array after each new message is added to feedSource
      this.messages = this.chat.conversation.asObservable()
      .pipe(scan((acc, val) => acc.concat(val)));

  }

  sendMessage() {
    if (this.countVal == 0) {
      this.sessionVal = "";
      this.countVal++;
    };

    // this.sessionVal = "";
    var cht = {text: this.formValue,
              session_val: this.sessionVal};
    // this.chat.sendMessage(cht).subscribe(res => console.log('entered value ==', res))
    if (this.countVal == 1) {
      this.chat.sendMessage(cht).subscribe(res => { console.log('response ====', res);
      let myObj = JSON.parse(JSON.stringify(res));

      this.sessionVal = myObj.session;
      this.countVal++;

      }); 
    } 
    this.chat.converse(cht);

    this.formValue = "";

  }

}


