import { Component, OnInit } from '@angular/core';
import { ChatService, Message } from 'src/app/chat.service';
import { Observable } from 'rxjs';
import { scan } from 'rxjs/operators';;

@Component({
  selector: 'app-chat-eng',
  templateUrl: './chat-eng.component.html',
  styleUrls: ['./chat-eng.component.css']
})
export class ChatEngComponent implements OnInit {

  messages: Observable<Message[]> | undefined;
  formValue!: string;

  constructor(private chat: ChatService) { }

  ngOnInit(): void {
      // var val = "friday";

      // var val = {
        // "text": "friday"
      // };
      // this.chat.talk(val).subscribe(res => console.log(res));


      // Appends to array after each new message is added to feedSource
      this.messages = this.chat.conversation.asObservable()
      .pipe(scan((acc, val) => acc.concat(val)));

  }

  sendMessage() {
    this.chat.converse(this.formValue);
    this.formValue = '';
  }

}
